	/*
	 * This code is used to declare a side length first as a int but then after was used to declare it as a double same with the volume.  
	 */
	
	//Task 1 is suppose calculate the surface area 
	double side=9.0;
	double surface=6*side*side;
	double surfacef=surface;
	
	
